FILEPATH = "todos.txt"def get_todos(filepath=FILEPATH):    """Read a text file and return the list of to-do items."""    try:        with open(filepath, 'r') as file_local:            # Strip newline characters from each line to clean up the todo items            todos_local = [line.strip() for line in file_local.readlines()]        return todos_local    except FileNotFoundError:        # Handle the case where the file does not exist gracefully        print(f"The file {filepath} was not found.")        return []    except Exception as e:        # General exception handling to catch other file reading errors        print(f"An error occurred while reading the file: {e}")        return []def write_todos(todos_arg, filepath=FILEPATH):    """Write the to-do items list in the text file."""    try:        with open(filepath, 'w') as file:            # Check if each todo item ends with a newline; if not, add one.            # This avoids adding an extra newline if it's already there.            todos_with_newlines = [f"{todo}\n" if not todo.endswith('\n') else todo for todo in todos_arg]            file.writelines(todos_with_newlines)    except Exception as e:        # General exception handling to catch file writing errors        print(f"An error occurred while writing to the file: {e}")# Example usage (uncomment to test)# todos = ['Read a book', 'Exercise for 30 minutes', 'Write in journal']# write_todos(todos)# print(get_todos())